import React from 'react';
import './App.css';
import RestaurantInfo from './components/RestaurantInfo';
import MenuItems from './components/MenuItems';
import Cart from './components/Cart';

function App() {
  return (
    <div className="App">
      <RestaurantInfo
        image={process.env.PUBLIC_URL + '/restaurant.jpg'} // تحميل الصورة من مجلد public
        name="Delicious Restaurant"
        description="Welcome to our delicious restaurant! Enjoy our amazing dishes."
      />
      <MenuItems />
      <Cart />
    </div>
  );
}

export default App;
