import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { setMenuItems } from '../store/menuSlice';
import { addToCart, incrementCartItem } from '../store/cartSlice'; // استيراد incrementCartItem
import './MenuItems.css';

function MenuItems() {
  const menuItems = useSelector(state => state.menuItems);
  const cartItems = useSelector(state => state.cart); // تحديث هنا

  const dispatch = useDispatch();

  useEffect(() => {
    fetch('/menuData.json')
      .then(response => response.json())
      .then(data => dispatch(setMenuItems(data)));
  }, [dispatch]);

  const handleAddToCartClick = (item) => {
    const existingCartItem = cartItems.find(cartItem => cartItem.id === item.id);

    if (existingCartItem) {
      dispatch(incrementCartItem({ id: item.id })); // زيادة العدد
    } else {
      dispatch(addToCart(item));
    }
  };

  return (
    <div className="menu-container">
      {menuItems.map(item => (
        <div className="menu-item" key={item.id}>
          <img src={item.image} alt={item.name} />
          <div className="menu-item-details">
            <p className="menu-item-name">{item.name}</p>
            <p className="menu-item-price">{item.price} $ </p>
          </div>
          <button onClick={() => handleAddToCartClick(item)}>Add to Cart</button>
        </div>
      ))}
    </div>
  );
}

export default MenuItems;
