import React from 'react';
import { useSelector } from 'react-redux';
import { addToCart } from '../store/cartSlice'; // Import addToCart action if needed

function Cart() {
    const cartItems = useSelector(state => state.cartItems);

    // حساب السعر الإجمالي
    const totalPrice = cartItems.reduce((total, item) => total + item.price, 0);

    return (
      <div className="cart">
        <h2>Cart</h2>
        <div className="cart-items">
          {cartItems.map(item => (
            <div className="cart-item" key={item.id}>
              <p>{item.name}</p>
              <p>{item.price}</p>
            </div>
          ))}
        </div>
        <div className="total-price">
          <p>Total Price:</p>
          <p>${totalPrice}</p> {/* عرض السعر الإجمالي */}
        </div>
      </div>
    );
  }

  export default Cart;
