import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { setMenuItems } from '../store/menuSlice';
import { addToCart } from '../store/cartSlice'; // Import addToCart function
import './MenuItems.css';

function MenuItems() {
  const menuItems = useSelector(state => state.menuItems);
  const dispatch = useDispatch();

  useEffect(() => {
    // Load data from JSON file (replace with API request if needed)
    fetch('/path/to/menuData.json')
      .then(response => response.json())
      .then(data => dispatch(setMenuItems(data)));
  }, [dispatch]);

  return (
    <div className="menu-container">
      {menuItems.map(item => (
        <div className="menu-item" key={item.id}>
          <img src={item.image} alt={item.name} />
          <div className="menu-item-details">
            <p className="menu-item-name">{item.name}</p>
            <p className="menu-item-price">{item.price}</p>
            <button onClick={() => dispatch(addToCart(item))}>Add to Cart</button>
          </div>
        </div>
      ))}
    </div>
  );
}

export default MenuItems;
